import{_ as a,c as i,o,a4 as l}from"./chunks/framework.CGsRFzUA.js";const u=JSON.parse('{"title":"Scenario of law and order","description":"","frontmatter":{},"headers":[],"relativePath":"en/work/english/02threshold_english/2-10scenario_lawandorder.md","filePath":"en/work/english/02threshold_english/2-10scenario_lawandorder.md"}'),r={name:"en/work/english/02threshold_english/2-10scenario_lawandorder.md"};function t(n,e,s,d,h,c){return o(),i("div",null,e[0]||(e[0]=[l('<h1 id="scenario-of-law-and-order" tabindex="-1">Scenario of law and order <a class="header-anchor" href="#scenario-of-law-and-order" aria-label="Permalink to &quot;Scenario of law and order&quot;">​</a></h1><h2 id="first-of-all" tabindex="-1">First of all <a class="header-anchor" href="#first-of-all" aria-label="Permalink to &quot;First of all&quot;">​</a></h2><ol><li>Talk about the legal age for activities using the passive of modal verbs.</li><li>Tablk about crime and punishment using the get passive.</li><li>Organize your views with <code>First of all</code>, etc.</li><li>Show someone has a valid argument with expressions like <code>That&#39;s a good point.</code> .</li></ol><h2 id="about-grammar" tabindex="-1">About grammar <a class="header-anchor" href="#about-grammar" aria-label="Permalink to &quot;About grammar&quot;">​</a></h2><ol><li><p>The passive of modal verbs for the present is modal verb + be + past participle. a. R-rated movies should be banned. b. The law ought to be changed.</p></li><li><p>THe passive is usaually formed with be, but sometimes you can use get.<br> a. Like : people who speed don&#39;t usually get arrested.</p></li><li><p>After should, the be passive is more common.<br> a. People who speed should be arrested if they cause an accident.</p></li><li><p>Notice : Use catch + person + verb + ing. a. Like : What happens if they catch you shoplifting.</p></li><li><p>Orgnization ideas a. Well,Basically ... The point/thing is ... b. Adding ideas : Another thing is ... c. Introducing a list : There are two problems ... or ... for a couple of reasons. d. Ordinal numbers : First of all, Second of all, ...<br> e. Numbers or letters : One, Two, ... or A, B, ...</p></li></ol><h2 id="about-content" tabindex="-1">About content <a class="header-anchor" href="#about-content" aria-label="Permalink to &quot;About content&quot;">​</a></h2><h2 id="new-words" tabindex="-1">New words <a class="header-anchor" href="#new-words" aria-label="Permalink to &quot;New words&quot;">​</a></h2><ol><li>penalty</li><li>jaywalking</li><li>vandals</li><li>kidnapping</li><li>robbing</li><li>shoplifting</li><li>convicted of burglary</li><li>graffiti</li><li>murderer</li><li>knife</li></ol><h2 id="new-phrases" tabindex="-1">New Phrases <a class="header-anchor" href="#new-phrases" aria-label="Permalink to &quot;New Phrases&quot;">​</a></h2><ol><li>catch someone doing</li><li>convict of(get sentenced to)</li><li>life in prison</li><li>lose your license</li><li>against the law</li><li>get enforced</li><li>put on probation</li><li>take someone captive</li><li>go to jail</li><li>get arrested</li><li>for a couple of reasons</li></ol>',10)]))}const f=a(r,[["render",t]]);export{u as __pageData,f as default};
