import{_ as e,c as a,o as i,a4 as r}from"./chunks/framework.svyMRVY3.js";const g=JSON.parse('{"title":"Sceranrio of ability","description":"","frontmatter":{},"headers":[],"relativePath":"en/work/english/03vantage_english/3-13scenario_ability.md","filePath":"en/work/english/03vantage_english/3-13scenario_ability.md"}'),l={name:"en/work/english/03vantage_english/3-13scenario_ability.md"};function n(o,t,s,d,h,c){return i(),a("div",null,t[0]||(t[0]=[r('<h1 id="sceranrio-of-ability" tabindex="-1">Sceranrio of ability <a class="header-anchor" href="#sceranrio-of-ability" aria-label="Permalink to &quot;Sceranrio of ability&quot;">​</a></h1><h2 id="about-content" tabindex="-1">About content <a class="header-anchor" href="#about-content" aria-label="Permalink to &quot;About content&quot;">​</a></h2><ol><li>talk about intelligence, skills, and abilities.</li><li>use adverbs and adjectives to describe and compare.</li><li>use vague expressions when you don&#39;t need to be precise.</li><li>agree with someone using responses like No doubt.</li></ol><h2 id="about-grammar" tabindex="-1">About grammar <a class="header-anchor" href="#about-grammar" aria-label="Permalink to &quot;About grammar&quot;">​</a></h2><ol><li><p>You can use adverbs like these before adjectives and adverbs to introduce these ideas.<br> a. Degree -- extremely, highly, relatively -- They&#39;re extremely literate and highly skilled.<br> b. Type -- scientifically, linguistically -- Linguistically intelligent people are good at languages.<br> c. Opinion -- incredibly, remarkably -- They seem to solve problems incredibly easily.<br> d. Focus -- especially, particularly -- People with logical intelligence are especially good at math.</p></li><li><p>Comparative and superlative,adjectives and adverbs</p><table tabindex="0"><thead><tr><th></th><th>Comparatives and as ... as</th><th>Superlatives</th></tr></thead><tbody><tr><td>Adjectives</td><td>I feel <strong>happier</strong>, and I&#39;ve gotten better at it.</td><td>The race is my biggest and best challenge.</td></tr><tr><td></td><td>I&#39;m <strong>more confident</strong> and <strong>less nervous</strong>.</td><td>Talking a course was <strong>the most sensible</strong> idea.</td></tr><tr><td></td><td>I&#39;m not <strong>as fast as</strong> I&#39;d like to be.</td><td>I was <strong>the least experienced</strong> presenter.</td></tr><tr><td>Adverbs</td><td>I&#39;m training <strong>harder</strong> than ever.</td><td>We all train hard, but I <strong>train</strong> hardest.</td></tr><tr><td></td><td>I&#39;m improving <strong>more slowly</strong> than I&#39;d hoped.</td><td>I train <strong>(the) most often</strong> on weekends.</td></tr><tr><td></td><td>I practice <strong>less often</strong> than I should.</td><td></td></tr><tr><td></td><td>I don&#39;t play <strong>as often as</strong> I should.</td><td><strong>(Superlative adverbs with least are not common)</strong></td></tr></tbody></table></li><li><p>vague expressions</p><table tabindex="0"><thead><tr><th>less common in formal</th><th>more common in formal</th></tr></thead><tbody><tr><td>... and things (like that)/ and stuff (like that)</td><td>and so on</td></tr><tr><td>... and all/ and all that</td><td>and all that</td></tr><tr><td>... and that kind of thing / and that sort of thing</td><td>and things</td></tr><tr><td>... and everything</td><td>and stuff</td></tr></tbody></table></li></ol><h2 id="new-words" tabindex="-1">New words <a class="header-anchor" href="#new-words" aria-label="Permalink to &quot;New words&quot;">​</a></h2><ol><li>rookie/noob</li><li>imprecise/inadequate</li><li>articulate(talking fluently)</li><li>literate(people who can read and write)</li><li>logical-mathematical</li><li>bodily</li><li>spatial/spacial/dimensional</li><li>interpersonal(interpersonal communication)</li><li>intrapersonal</li><li>vague/unclear</li><li>unscramble</li></ol><h2 id="new-phrases" tabindex="-1">New Phrases <a class="header-anchor" href="#new-phrases" aria-label="Permalink to &quot;New Phrases&quot;">​</a></h2><ol><li>be adequate for</li><li>be sensitive to</li><li>be skilled at</li><li>be scientifically minded</li><li>have a capacity for</li><li>be efficient at</li><li>have a talent for</li><li>be particularly adept at</li></ol>',9)]))}const m=e(l,[["render",n]]);export{g as __pageData,m as default};
