import{_ as i,c as t,a4 as a,o as l}from"./chunks/framework.C3yiUNUH.js";const u=JSON.parse('{"title":"scernario stories","description":"","frontmatter":{},"headers":[],"relativePath":"en/work/english/03vantage_english/3-4scenario_stories.md","filePath":"en/work/english/03vantage_english/3-4scenario_stories.md"}'),o={name:"en/work/english/03vantage_english/3-4scenario_stories.md"};function n(r,e,s,c,h,p){return l(),t("div",null,e[0]||(e[0]=[a('<h1 id="scernario-stories" tabindex="-1">scernario stories <a class="header-anchor" href="#scernario-stories" aria-label="Permalink to &quot;scernario stories&quot;">​</a></h1><h2 id="first-of-all" tabindex="-1">First of all <a class="header-anchor" href="#first-of-all" aria-label="Permalink to &quot;First of all&quot;">​</a></h2><ol><li>talk about life lessions and experiences</li><li>use the past tense, the present perfect, and the past perfect.</li><li>make comments when telling a story.</li><li>use (It&#39;s) no wonder ... to say something is not surprising.</li></ol><h2 id="about-grammar" tabindex="-1">About grammar <a class="header-anchor" href="#about-grammar" aria-label="Permalink to &quot;About grammar&quot;">​</a></h2><ol><li><p>What&#39;s the difference between present prefect and present prefect continous.<br> a. present prefect : until now, I might turn to the other thing or keep on doing the same thing.<br> b. present perfect continous : until now, I might continously keep on the same thing.</p></li><li><p>Use the past tense for situations and events that are part of a completed past time, not connected to now.<br> a. I lived in Italy a few years ago.(I&#39;m not there now).</p></li><li><p>Use past present perfect for situations and events that are part of a past time that continues up to now.<br> a. Life has been interesting so far. (It still is)</p></li><li><p>Use simple verbs for completed events or permanent situations.<br> a. Marco&#39;s band broke up.</p></li><li><p>Use continuous verbs for background, on going, or temporary events or situations.<br> a. He&#39;s been coaching a soccer team.</p></li></ol><h2 id="about-content" tabindex="-1">About content <a class="header-anchor" href="#about-content" aria-label="Permalink to &quot;About content&quot;">​</a></h2><h2 id="new-words" tabindex="-1">New words <a class="header-anchor" href="#new-words" aria-label="Permalink to &quot;New words&quot;">​</a></h2><ol><li>miserable</li><li>penalty</li><li>hall</li><li>ethics</li><li>deterrent</li><li>anecdote</li><li>gymnastics</li><li>pervasive</li><li>evolve</li><li>spot</li><li>determine</li><li>convention</li><li>bartenders</li><li>sibling</li><li>literal</li><li>rigidly</li><li>infer</li><li>anonymous</li><li>analogous</li><li>jargon</li><li>abbreviation</li><li>facilitate</li><li>amortize</li></ol><h2 id="new-phrases" tabindex="-1">New Phrases <a class="header-anchor" href="#new-phrases" aria-label="Permalink to &quot;New Phrases&quot;">​</a></h2><ol><li>woke/waked up(woke&#39;s more common in use)</li><li>life&#39;s ups and downs</li><li>cheer you up/make you happy</li><li>break up(end up)</li><li>count toward our final grade</li><li>struggling with the class</li><li>fall behind on my work</li><li>caught up on</li><li>turn in(hand on)</li><li>left the question blank</li><li>raised his hand</li><li>affect my grade</li><li>complete that questions</li><li>count towards</li><li>turn to(going to)</li><li>turn into (water turns into tea and is changed totally)</li><li>be convicted of (get sentenced to)</li><li>right before ... + clause with simple past</li></ol>',10)]))}const m=i(o,[["render",n]]);export{u as __pageData,m as default};
